import React, { useEffect, useState } from 'react';
import fetch_token from './fetch_token.js'; 
import fetch_calendar from './calendar.js';

const App = () => {
  const username = "test07"; // 假设的用户名
  const team = "김천"; // 假设的团队名
  const [token, setToken] = useState('');
  const [Local_token, setLocalToken] = useState(localStorage.getItem('token') || '');
  const [Local_name, setLocalName] = useState(localStorage.getItem('username') || '');
  const [Local_team, setLocalTeam] = useState(localStorage.getItem('team') || '');
  const [calendarData,setcalendarData] = useState(null);

  useEffect(() => {
    const getTokenInfo = async () => {
      // 如果用户名或团队不同，则重新获取 token
      if (username !== Local_name || team !== Local_team) {
        const fetchedToken = await fetch_token(username, team);
        setToken(fetchedToken);
        setLocalToken(fetchedToken);
        setLocalName(username);
        setLocalTeam(team);

        // 存储到 localStorage 以便下次使用
        localStorage.setItem('token', fetchedToken);
        localStorage.setItem('username', username);
        localStorage.setItem('team', team);
      } else {
        setToken(Local_token);   //token value
      }
    };
    getTokenInfo(); // 调用获取 token 的函数
  }, [username, team, Local_name, Local_team, Local_token]); // 依赖项列表


  useEffect(() => {
    const getcalendarInfo = async () => {
      const fetchcalendar = await fetch_calendar(token);
      setcalendarData(fetchcalendar);
    };
    getcalendarInfo(); // 调用获取 token 的函数
  }, [token]); // 依赖项列表


  return (
    <div>
      <h3>Information</h3>
      <p>Token: {token}</p>

      <h3>Past Events</h3>
      <p>calendar: {calendarData}</p>
    </div>
  );
};

export default App;
