import React, { useEffect, useState } from 'react';
import fetch_token from './fetch_token.js'; // 导入获取 token 的函数
import fetch_calendar from './calendar.js'; // 导入获取日历的函数

function App() {
    const username = "1wadadwa5219";
    const [calendarData, setCalendarData] = useState([]);
    const [token, setToken] = useState(localStorage.getItem('access_token'));
    const [message, setMessage] = useState(localStorage.getItem('msg'));

    useEffect(() => {
        const getToken = async () => {
            // 检查本地是否已有 token
            if (!token) {
                await fetch_token(username, "울산"); // 如果没有 token，则请求 token
                const newToken = localStorage.getItem('access_token');
                const newMessage = localStorage.getItem('msg');
                setToken(newToken);
                setMessage(newMessage);
            }
        };
        getToken();
    }, [username, token]); // 当 username 或 token 变化时执行

    useEffect(() => {
        const fetchData = async () => {
            if (token) {
                try {
                    const data = await fetch_calendar(token);
                    setCalendarData(data); // 存储获取到的日历数据
                } catch (error) {
                    console.error("Error fetching calendar data:", error);
                }
            }
        };
        fetchData();
    }, [token]); // 当 token 变化时执行

    return (
        <div>
            <h3>Information</h3>
            <p>Token: {token || "No token available"}</p>
            <p>Message: {message || "No message available"}</p>

            <h3>Past Events</h3>
            <ul>
                {Array.isArray(calendarData) && calendarData.map(event => (
                    <li key={event.id}>
                        {event.title} - {event.date}
                    </li>
                ))}
                {!Array.isArray(calendarData) && <li>No events found.</li>}
            </ul>
        </div>
    );
}

export default App;
